import { useState } from "react";
import styles from './select.module.css';

export const Select = ({value, onChange, options, type}) => {
  const [isOpen, setIsOpen] = useState(true);
  return (
    <>    
    <div className={styles.container} onClick={() => setIsOpen(prev => !prev)} >
      <span className={styles.value}>{value.label}</span>
      <button className={styles["clear-btn"]}>&times;</button>
      <div className={styles.divider}></div>
      <div className={styles.caret}></div>
      <ul className={`${styles.options} ${isOpen ? styles.show : ""}`}>
        {options.map((option, index) => (
          <li className={styles.option} key={index}>            
            {type === "Color" ? <span>{option.label}</span> : <span style={{backgroundColor:option.label}}>{option.label}</span>}
          </li>
        ))}
      </ul>
    </div>
    </>
  )
}
